"use client"

import React from 'react';

const Loader = () => {
  return (
    <div className="relative rounded-full" style={{
      transform: 'scale(0.5)',
    }}>
      <div className="absolute top-1 left-0 w-[100px] h-[100px] rounded-full bg-primary/20 dark:bg-primary/10" style={{
        boxShadow: 'inset 0 10px 10px 0 hsl(var(--primary)/0.5), inset 0 -10px 10px 0 hsl(var(--primary)/0.9)'
      }} />
      
      <svg width={100} height={100} viewBox="0 0 100 100" className="absolute">
        <defs>
          <mask id="clipping">
           
            <polygon points="50,25 75,75 25,75" fill="white" className="blur-[7px] origin-[50%_50%] animate-[rotation_2s_linear_infinite_reverse]" />
            <polygon points="35,35 65,35 50,65" fill="white" className="blur-[7px] origin-[50%_60%] animate-[rotation_2s_linear_infinite] [animation-delay:-0.67s]" />
            <polygon points="35,35 65,35 50,65" fill="white" className="blur-[7px] origin-[40%_40%] animate-[rotation_2s_linear_infinite_reverse]" />
            <polygon points="35,35 65,35 50,65" fill="white" className="blur-[7px] origin-[40%_40%] animate-[rotation_2s_linear_infinite_reverse] [animation-delay:-1s]" />
            <polygon points="35,35 65,35 50,65" fill="white" className="blur-[7px] origin-[60%_40%] animate-[rotation_2s_linear_infinite]" />
            <polygon points="35,35 65,35 50,65" fill="white" className="blur-[7px] origin-[60%_40%] animate-[rotation_2s_linear_infinite] [animation-delay:-1.33s]" />
          </mask>
        </defs>
      </svg>
      
      <div className="w-[100px] h-[100px] bg-primary" style={{
        mask: 'url(#clipping)',
        WebkitMask: 'url(#clipping)'
      }} />
      
      <style jsx>{`
        @keyframes rotation {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

export default Loader;
