"use client";

import React, { useRef, useState, useEffect } from 'react';
import { cn } from "@/lib/utils";

/**
 * "LIQUID CHROME" TEXT
 * Static geometry, but fluid internal lighting.
 * The gradient creates a mercury-like effect that follows the mouse light source.
 */

interface TextAnimationProps {
  text?: string;
  className?: string;
  fontSize?: number;
}

export default function TextAnimation({ 
  text = "RARE UI", 
  className,
  fontSize = 120
}: TextAnimationProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [gradientPos, setGradientPos] = useState({ x: 50, y: 50 });

  const handleMouseMove = (e: React.MouseEvent) => {
    const rect = containerRef.current?.getBoundingClientRect();
    if (rect) {
      const x = ((e.clientX - rect.left) / rect.width) * 100;
      const y = ((e.clientY - rect.top) / rect.height) * 100;
      setGradientPos({ x, y });
    }
  };

  const handleMouseLeave = () => {
    setGradientPos({ x: 50, y: 50 });
  };

  return (
    <div 
      ref={containerRef}
      className={cn("relative flex items-center justify-center p-20 cursor-default bg-black min-h-[300px]", className)}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <div 
        className="relative z-10 font-black text-center select-none tracking-tighter"
        style={{ 
          fontSize: fontSize,
          // Premium Multi-Layer Gradient
          backgroundImage: `
            radial-gradient(
              circle at ${gradientPos.x}% ${gradientPos.y}%, 
              #ffffff 0%,    
              #e2e8f0 25%,   
              #94a3b8 50%,   
              #475569 75%,   
              #0f172a 100%   
            )
          `,
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          backgroundClip: "text",
          color: "transparent",
          // Deep metallic shadow
          filter: "drop-shadow(0px 10px 20px rgba(0,0,0,0.5))"
        }}
      >
        {text}
        
        {/* Reflection Layer (Sheen) */}
        <div 
           className="absolute inset-0 pointer-events-none mix-blend-overlay opacity-50"
           aria-hidden="true"
           style={{
             backgroundImage: `
               linear-gradient(
                 ${gradientPos.x * 3.6}deg, 
                 transparent 40%, 
                 rgba(255,255,255,0.8) 50%, 
                 transparent 60%
               )
             `,
             WebkitBackgroundClip: "text",
             backgroundClip: "text",
           }}
        >
          {text}
        </div>
      </div>
      
      {/* Dynamic Glow behind */}
      <div 
        className="absolute w-[50%] h-[50%] blur-[100px] pointer-events-none transition-all duration-300 ease-out opacity-20"
        style={{
          background: "radial-gradient(circle, #38bdf8 0%, transparent 70%)",
          left: `${gradientPos.x}%`,
          top: `${gradientPos.y}%`,
          transform: "translate(-50%, -50%)"
        }}
      />
    </div>
  );
}
